DROP TABLE IF EXISTS `sys_menu`;
CREATE TABLE `sys_menu` (
  `ID` varchar(50) NOT NULL COMMENT 'ID',
  `PARENT_ID` varchar(50) NOT NULL COMMENT '上级菜单ID',
  `PARENT_IDS` varchar(2000) NOT NULL COMMENT '所有上级菜单ID',
  `NAME` varchar(50) NOT NULL COMMENT '菜单名称',
  `HREF_TYPE` varchar(50) DEFAULT NULL COMMENT '连接类型',
  `HREF` varchar(2000) DEFAULT NULL COMMENT '连接地址',
  `SORT` int(11) DEFAULT NULL COMMENT '排序',
  `SHOW_FLAG` varchar(1) DEFAULT NULL COMMENT '是否可见',
  `PERMISSION` varchar(200) DEFAULT NULL COMMENT '权限标识',
  `TARGET` varchar(50) DEFAULT NULL COMMENT '目标',
  `ICON` varchar(50) DEFAULT NULL COMMENT '图标',
  `CREATE_BY` varchar(50) DEFAULT NULL COMMENT '创建者',
  `CREATE_DATE` datetime DEFAULT NULL COMMENT '创建日期',
  `UPDATE_BY` varchar(50) DEFAULT NULL COMMENT '修改者',
  `UPDATE_DATE` datetime DEFAULT NULL COMMENT '修改日期',
  `REMARKS` varchar(50) DEFAULT NULL COMMENT '备注',
  `DEL_FLAG` varchar(1) NOT NULL DEFAULT '0' COMMENT '删除标记',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

