-- ----------------------------
-- Table structure for `sys_office`
-- ----------------------------
DROP TABLE IF EXISTS `sys_office`;
CREATE TABLE `sys_office` (
  `ID` varchar(50) NOT NULL COMMENT 'ID',
  `PARENT_ID` varchar(50) NOT NULL COMMENT '上级机构ID',
  `PARENT_IDS` varchar(2000) NOT NULL COMMENT '所有上级机构ID',
  `NAME` varchar(50) NOT NULL COMMENT '机构名称',
  `GFJC` varchar(50) DEFAULT NULL COMMENT '规范简称',
  `ZZJGDM` varchar(50) DEFAULT NULL COMMENT '组织机构代码',
  `JGXZ` varchar(50) DEFAULT NULL COMMENT '机构性质',
  `ZZRSBM` varchar(50) DEFAULT NULL COMMENT '组织人事部门',
  `STATE_FLAG` varchar(1) NOT NULL COMMENT '机构状态',
  `VERSION` varchar(50) DEFAULT NULL COMMENT '使用版本',
  `SORT` int(11) DEFAULT NULL COMMENT '排序',
  `CREATE_BY` varchar(50) DEFAULT NULL COMMENT '创建者',
  `CREATE_DATE` datetime DEFAULT NULL COMMENT '创建日期',
  `UPDATE_BY` varchar(50) DEFAULT NULL COMMENT '修改者',
  `UPDATE_DATE` datetime DEFAULT NULL COMMENT '修改日期',
  `REMARKS` varchar(50) DEFAULT NULL COMMENT '备注',
  `DEL_FLAG` varchar(1) NOT NULL DEFAULT '0' COMMENT '删除标记',
  `CHANGE_REASON` varchar(2) DEFAULT NULL COMMENT '变更原因',
  PRIMARY KEY (`ID`),
  KEY `AK_NAME` (`NAME`) USING BTREE,
  KEY `ID_INDEX` (`ID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

